package vn.edu.iuh.fit.sms;import com.twilio.Twilio;import com.twilio.rest.api.v2010.account.Message;import com.twilio.type.PhoneNumber;import org.springframework.stereotype.Service;@Servicepublic class TwilioSMSService {    public static final String ACCOUNT_SID = "ACbc85c10004ce628d4e8cb0e1c6777dd0";    public static final String AUTH_TOKEN = "fd2704b681756f146a344263db90295b";    public static final String TWILIO_PHONE_NUMBER = "+12242573852";    static {        Twilio.init(ACCOUNT_SID, AUTH_TOKEN);    }    public void sendPasswordViaSMS(String to, String password) {        String messageBody = String.format("Mật khẩu mới của bạn là: %s", password);        Message.creator(                new PhoneNumber(to),                new PhoneNumber(TWILIO_PHONE_NUMBER),                messageBody        ).create();    }}