package vn.edu.iuh.fit.sms;import com.vonage.client.VonageClient;import com.vonage.client.VonageClientException;import com.vonage.client.sms.MessageStatus;import com.vonage.client.sms.SmsSubmissionResponse;import com.vonage.client.sms.messages.TextMessage;import org.springframework.beans.factory.annotation.Value;import org.springframework.stereotype.Service;@Servicepublic class VonageService {    private final VonageClient vonageClient;    public VonageService() {        this.vonageClient = VonageClient.builder().apiKey("51a3b160").apiSecret("FXtMEaKaJa3iasDF").build();    }    public void sendPasswordViaSMS(String to, String password) {        System.out.println("Phone number: " + to);        String messageBody = String.format("Mật khẩu mới của bạn là: %s", password);        TextMessage message = new TextMessage("Vonage APIs", to, messageBody);        try {            SmsSubmissionResponse response = vonageClient.getSmsClient().submitMessage(message);            System.out.println(response);            if (response.getMessages().get(0).getStatus() == MessageStatus.OK) {                System.out.println("Message sent successfully.");            } else {                System.out.println("Message failed with error: " + response.getMessages().get(0).getErrorText());            }        } catch (VonageClientException e) {            System.err.println("Failed to send SMS: " + e.getMessage());        }    }}